        $member_id = $_SESSION['member_id'];

        // Get member profile
        $profile_result = $conn->query("SELECT * FROM member_profiles WHERE member_id = $member_id");
        $profile = $profile_result->fetch_assoc();

        // Update profile
        if ($_SERVER['REQUEST_METHOD'] == 'POST') {
            $bio = $conn->real_escape_string($_POST['bio']);
                $skills = $conn->real_escape_string($_POST['skills']);
                    $achievements = $conn->real_escape_string($_POST['achievements']);
                        $social_links = $conn->real_escape_string($_POST['social_links']);
                            
                                if ($profile) {
                                        // Update existing profile
                                                $sql = "UPDATE member_profiles SET bio = '$bio', skills = '$skills', achievements = '$achievements', social_links = '$social_links' WHERE member_id = $member_id";
                                                    } else {
                                                            // Create new profile
                                                                    $sql = "INSERT INTO member_profiles (member_id, bio, skills, achievements, social_links) VALUES ($member_id, '$bio', '$skills', '$achievements', '$social_links')";
                                                                        }
                                                                            
                                                                                if ($conn->query($sql) === TRUE) {
                                                                                        $_SESSION['message'] = "Profile updated successfully!";
                                                                                                $_SESSION['message_type'] = "success";
                                                                                                        header("Location: profile.php");
                                                                                                                exit();
                                                                                                                    } else {
                                                                                                                            $error = "Failed to update profile. Please try again.";
                                                                                                                                }
                                                                                                                                }
