// register.php
$page_title = "Register | KNUST Internship Portfolio";
include 'includes/config.php';

// Redirect if already logged in
if (isLoggedIn()) {
    header("Location: member/dashboard.php");
        exit();
        }

        // Process registration
        if ($_SERVER['REQUEST_METHOD'] == 'POST') {
            $student_id = $conn->real_escape_string($_POST['student_id']);
                $full_name = $conn->real_escape_string($_POST['full_name']);
                    $email = $conn->real_escape_string($_POST['email']);
                        $phone = $conn->real_escape_string($_POST['phone']);
                            $course = $conn->real_escape_string($_POST['course']);
                                $year_of_study = $conn->real_escape_string($_POST['year_of_study']);
                                    $password = $_POST['password'];
                                        $confirm_password = $_POST['confirm_password'];
                                            
                                                // Validate passwords match
                                                    if ($password !== $confirm_password) {
                                                            $error = "Passwords do not match!";
                                                                } else {
                                                                        // Check if student ID or email already exists
                                                                                $check_user = $conn->query("SELECT * FROM members WHERE student_id = '$student_id' OR email = '$email'");
                                                                                        
                                                                                                if ($check_user->num_rows > 0) {
                                                                                                            $error = "Student ID or Email already exists!";
                                                                                                                    } else {
                                                                                                                                // Hash password
                                                                                                                                            $hashed_password = password_hash($password, PASSWORD_DEFAULT);
                                                                                                                                                        $verification_token = bin2hex(random_bytes(32));
                                                                                                                                                                    
                                                                                                                                                                                // Insert new member
                                                                                                                                                                                            $sql = "INSERT INTO members (student_id, full_name, email, phone, course, year_of_study, password, verification_token) 
                                                                                                                                                                                                                VALUES ('$student_id', '$full_name', '$email', '$phone', '$course', '$year_of_study', '$hashed_password', '$verification_token')";
                                                                                                                                                                                                                            
                                                                                                                                                                                                                                        if ($conn->query($sql) === TRUE) {
                                                                                                                                                                                                                                                        $member_id = $conn->insert_id;
                                                                                                                                                                                                                                                                        
                                                                                                                                                                                                                                                                                        // Create empty profile
                                                                                                                                                                                                                                                                                                        $conn->query("INSERT INTO member_profiles (member_id) VALUES ($member_id)");
                                                                                                                                                                                                                                                                                                                        
                                                                                                                                                                                                                                                                                                                                        $_SESSION['message'] = "Registration successful! You can now login.";
                                                                                                                                                                                                                                                                                                                                                        $_SESSION['message_type'] = "success";
                                                                                                                                                                                                                                                                                                                                                                        header("Location: login.php");
                                                                                                                                                                                                                                                                                                                                                                                        exit();
                                                                                                                                                                                                                                                                                                                                                                                                    } else {
                                                                                                                                                                                                                                                                                                                                                                                                                    $error = "Registration failed. Please try again.";
                                                                                                                                                                                                                                                                                                                                                                                                                                }
                                                                                                                                                                                                                                                                                                                                                                                                                                        }
                                                                                                                                                                                                                                                                                                                                                                                                                                            }
                                                                                                                                                                                                                                                                                                                                                                                                                                            }
